INSERT INTO main.reviews (id, title, platform_id, game_id, created_at, views, video, introduction, content, conclusion, positive, negative, url) VALUES (99, 'Witcher 2 - Assassins of Kings Review by Leinchetzu', 6, 478, '2015-01-22 10:45:02.348000', 181, 'None', 'Hello , my name is Nick and i''m here to introduce you to the world of The Witcher. I first started to play this game 2 years ago on my crap PC and it worked OK for few runs , but the graphics card eventually couldn''t keep up anymore and i was really sad. I wanted to play it so much so i waited and last year I finally managed to play the game 60 fps maxed out. 
