INSERT INTO main.studio (id, name, platform_id, logo, description, created_at, website) VALUES (831, 'Access Games', 6, 'https://images.igdb.com/igdb/image/upload//vv5kurn6xv3nstbpdxtn.png', 'Access Games Inc. was founded on 16th of January, 2002, and are Japanese, based in Osaka / Japan. They are most known for Deadly Premonition (an open world survival horror game, also called Red Seeds Profile in Japan). One of the company''s most notable members is Hidetaka "SWERY" Suehiro, who was the writer and director of Spy Fiction and Deadly Premonition. Access Games are also behind D4: Dark Dreams Don''t Die.', '2012-09-02 10:31:34.296000', 'http://www.accessgames.co.jp/');
