INSERT INTO main.studio (id, name, platform_id, logo, description, created_at, website) VALUES (858, 'Koei Tecmo Games Co., Ltd.', 6, 'https://images.igdb.com/igdb/image/upload//xuzi6vyb4x2rg67eezs5.png', '"In April 1, 2010, Tecmo was declared disbanded in Japan. Its sister company Koei survived but was renamed Koei Tecmo Games and is now the sole publishing arm of the group. Koei Tecmo Games continues to strategically use the "Tecmo" and "Koei" brand names on new video games for marketing purposes. In addition, the former development divisions of Tecmo and Koei were briefly spun-off as separate companies in March 2010, but folded into Tecmo Koei Games in April 2011"', '2012-09-30 10:37:55.401000', 'None');
