INSERT INTO main.studio (id, name, platform_id, logo, description, created_at, website) VALUES (5318, 'Wanako Studios Ltd.', 36, 'https://images.igdb.com/igdb/image/upload//mngcv2b6npjk3dcmszha.png', 'Wanako Studios Ltd. was founded in 2002, by Esteban Sosnik, Tiburcio de la Carcova, and Wenceslao Casares. It was registered in New York City and headquartered in Santiago, Chile, until the studio was acquired by Sierra Entertainment in 2007. The company was then absorbed into Activision Blizzard and later acquired by Artificial Mind and Movement in 2008. When Artificial Mind and Movement became Behaviour Interactive in 2010, Wanako Studios was renamed Behaviour Santiago.', '2015-04-24 00:05:54.163000', 'None');
INSERT INTO main.studio (id, name, platform_id, logo, description, created_at, website) VALUES (5319, 'Sierra Online', 36, 'https://images.igdb.com/igdb/image/upload//khqgbrvvihdwfkquvs9p.png', 'Sierra Online, not to be confused with Sierra On-Line/Sierra Entertainment, was established in 2006 as a division of Vivendi Universal Games, and focused on publishing online titles. In 2008, when its parent company Vivendi Games merged with Activision to form Activision Blizzard, the entire Sierra Online division was dissolved.', '2015-04-24 00:05:54.203999', 'None');
